function verficar_guia($serie_guia)
{
$_SESSION['scriptcase']['form_del_factura']['contr_erro'] = 'on';
  
if($serie_guia<>''){
	if(strlen($serie_guia)==17){
		$porciones = explode("-", $serie_guia);
		if (count($porciones)==3){
			if (strlen($porciones[0])==3 and strlen($porciones[1])==3 and strlen($porciones[2])==9 ){
				return true;
			}else{
				return false;
			}
		}else{
			return false;
		}	
	}else{
		return false;
	}
}else{
	return true;
}

$_SESSION['scriptcase']['form_del_factura']['contr_erro'] = 'off';
}
