        if ($_SESSION['sc_session'][$this->Ini->sc_page]['grid_resumen_ventas_usuario']['ajax_nav'])
        { 
           $_SESSION['scriptcase']['saida_html'] = "";
        } 
       $this->NM_case_insensitive = true;
       $tmp = $_SESSION['sc_session'][$this->Ini->sc_page]['grid_resumen_ventas_usuario']['cond_pesq'];
       $pos = strrpos($tmp, "##*@@");
       if ($pos !== false)
       {
           $and_or = (substr($tmp, ($pos + 5)) == "and") ? $this->Ini->Nm_lang['lang_srch_and_cond'] : $this->Ini->Nm_lang['lang_srch_orr_cond'];
           $tmp    = substr($tmp, 0, $pos);
           $this->grid_search_str = str_replace("##*@@", ", " . $and_or . " ", $tmp);
       }
       $str_display = empty($_SESSION['sc_session'][$this->Ini->sc_page]['grid_resumen_ventas_usuario']['grid_pesq'])?'none':'';
       if(!$_SESSION['sc_session'][$this->Ini->sc_page]['grid_resumen_ventas_usuario']['ajax_nav'])
       {
          $nm_saida->saida("   <tr id=\"NM_Grid_Search\" ajax='' style='display:" . $str_display . "'>\r\n");
       }
       if ($_SESSION['sc_session'][$this->Ini->sc_page]['grid_resumen_ventas_usuario']['ajax_nav'] && !empty($_SESSION['sc_session'][$this->Ini->sc_page]['grid_resumen_ventas_usuario']['grid_pesq']))
       { 
           $_SESSION['scriptcase']['saida_html'] = "";
