          }
       }
       if (isset($_SESSION['sc_session'][$this->Ini->sc_page]['grid_resumen_ventas_usuario']['grid_pesq']) && count($_SESSION['sc_session'][$this->Ini->sc_page]['grid_resumen_ventas_usuario']['grid_pesq']) < 6)
       {
           $lin_obj .= "<table id='id_grid_search_all_cmp' cellpadding=0 cellspacing=0>";
